@Override
public Sales processItem(Object s) {
    Sales sales = new Sales();
    StringTokenizer tokens = new StringTokenizer((String)s, ",");
    sales.setId(Integer.parseInt(tokens.nextToken()));
    sales.setAmount(Float.parseFloat(tokens.nextToken()));

    return sales;
}

